std::wstring ADisPawn::GetMyDocumentsFolderPath()
{
	wchar_t Folder[1024];
	HRESULT hr = SHGetFolderPathW(0, CSIDL_MYDOCUMENTS, 0, 0, Folder);
	if (SUCCEEDED(hr))
	{
		return Folder;
	}
	else return L"";
}
std::string ADisPawn::CreateDirectiryToStudentsLogsFiles()
{


	std::string path = spathToDocuments + "\\Dispetcher_StudentsLogsFiles";



	if (_mkdir(path.c_str()))
	{
		return path;
	}
	return path;
}

