	RemoveFile(spathTo__DirectoryStudentsLogsFiles + "\\Karabas.txt");
	RemoveFile(spathTo__DirectoryStudentsLogsFiles + "\\Karaganozek.txt");
	RemoveFile(spathTo__DirectoryStudentsLogsFiles + "\\Rasporiaditelnaya.txt");
	RemoveFile(spathTo__DirectoryStudentsLogsFiles + "\\GanaKaraganda.txt");
	RemoveFile(spathTo__DirectoryStudentsLogsFiles + "\\Karaganda_1.txt");
	RemoveFile(spathTo__DirectoryStudentsLogsFiles + "\\Karaganda_2.txt");
