Commd ADisPawn::GetCommFromRoleSelect(RoleSelect role)
{
	if (role == RoleSelect::Role_Curator) return Commd::Curator;
	else if (role == RoleSelect::Role_Dispetcher_1) return Commd::Dispetcher_1;
	else if (role == RoleSelect::Role_Dispetcher_2) return Commd::Dispetcher_2;
	else if (role == RoleSelect::Role_Karabas) return Commd::Karabas;
	else if (role == RoleSelect::Role_Karaganozek) return Commd::Karaganozek;
	else if (role == RoleSelect::Role_Karaganda) return Commd::Karaganda;
	else if (role == RoleSelect::Role_Maykuduk) return Commd::Maykuduk;
	else if (role == RoleSelect::Role_GanaKaraganda) return Commd::GanaKaraganda;
	else if (role == RoleSelect::Role_Karaganda_1) return Commd::Karaganda_1;
	else if (role == RoleSelect::Role_Karaganda_2) return Commd::Karaganda_2;
	else if (role == RoleSelect::Role_Rasporiaditelnaya) return Commd::Rasporiaditelnaya;
	else if (role == RoleSelect::Role_Dubovskaya) return Commd::Dubovskaya;
	else if (role == RoleSelect::Role_GanaAul) return Commd::GanaAul;
	else if (role == RoleSelect::Role_Radiouzel) return Commd::Radiouzel;
	return Commd::NotDef;
}

