// Called every frame
void ADisPawn::Tick(float DeltaTime)
{
	Super::Tick(DeltaTime);

	krz_pult_CheckerSetterDirectRouter();
	DopCheckerForPult();
	krz_Pult_CheckerChanger();

	krb_pult_CheckerSetterDirectRouter();
	DopCheckerForPult();
	krb_Pult_CheckerChanger();

	if (IsServer)
	{
		CheckSetGreenKeys();
		message = "ServerConnectionsTo: ";
		for (size_t i = 0; i < NameConnections.size(); i++)
		{
			message = message + GetStationName(NameConnections[i].station) + " ";
		}
		//GEngine->AddOnScreenDebugMessage(-1, DeltaTime, FColor::Black, message);

	}
	else if (newConnection.status)
	{
		message = "connected to server";
		//GEngine->AddOnScreenDebugMessage(-1, DeltaTime, FColor::Green, message);
	}
	else
	{
		message = "no connect";
		//GEngine->AddOnScreenDebugMessage(-1, DeltaTime, FColor::Red, message);

	}
}

