FString ADisPawn::GetIPAddressFromDisk()
{
	std::ifstream ifs;

	ifs.open("address.txt");
	if (!ifs.is_open())
	{
		//std::cout << "Не удалось открыть файл для чтения адреса локальной сети" << std::endl;
		//BeeperBad(2);
		address = WriteAddressFromUser("192.168.88.241");
		ifs.close();
		return address;
	}
	else
	{
		std::string tmp;
		ifs >> tmp;
		ifs.close();
		if (tmp.size() == 0)
		{
			address = WriteAddressFromUser("127.0.0.1");
		}
		else
		{
			address = tmp.c_str();
		}
		return address;
	}
}
FString ADisPawn::WriteAddressFromUser(FString ipaddr)
{
	std::ofstream ofs;


	ofs.open("address.txt");
	if (!ofs.is_open())
	{
		//std::cout << "Ошибка открытия файла для записи\n";
		////BeeperBad(1);
	}
	else
	{
		address = ipaddr;
		std::string const tmp = TCHAR_TO_UTF8(*address);
		ofs << tmp;
		//BeeperGood3();
	}
	return address;
	ofs.close();
}
