void ADisPawn::ServerStart() {

	cicleRun = true;
	std::cout << "Server: Curator: OK !" << std::endl;
	stationOnline.Curator_onLine = true;
	while (cicleRun)
	{

		SOCKET newconnect = accept(sListen, (SOCKADDR*)&addr, &sizeofaddr);

		if (newconnect == INVALID_SOCKET)
		{
			//BeeperBad(3);
			//std::cout << newConnection.connection << std::endl;
			//DeleteWrongAddress();
			break;
		}
		else {
			Station station = AskNameStation(newconnect);



			MyRecv(newconnect, (char*)&student, sizeof(FStudent));

			if (RegisterStation(station))
			{

				PrintDataStudentToLog(station, student);
				AddDataStudentToDatesStudent(station, student);
				Connections.push_back(newconnect);
				NameConnect nameConnect = {};
				nameConnect.connection = newconnect;
				nameConnect.station = station;
				NameConnections.push_back(nameConnect);

				SendDataAllStationToOne(newconnect);

				std::thread t(&ADisPawn::ClientHandler, this, nameConnect.connection /*newconnect*/);
				t.detach();
			}
			else//отказ в регистрации на сервере
			{
				char FirstCode = 'n';
				MySend(newconnect, (char*)&FirstCode, sizeof(char));
			}
		}
	}

}

