void ADisPawn::ClientHandler(SOCKET connect) {
	char FirstCode = 'a';
	int Sizebyte = 0;
	int NumberTrain = 0;
	int NumberWay = 0;
	int operCountVagForMinus = 0;
	int sizeOfData = 0;
	FOtcep check = {};
	//Station connectStation = connect->station;
	Station station = Station::Unknown;
	Commd comm = Commd::NotDef;
	FDataZaprosAddOtcep zaprosAddOtcep;
	FDataZaprosMinusOtcep zaprosMinusOtcep;
	ZaprosFromSortirovka zaprosSort;
	while (cicleRun) {
		Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&FirstCode, sizeof(char));
		if (Sizebyte == SOCKET_ERROR)//проверяем возврат send на ошибку
		{

			/*FString mess = "Server: MyRecv: Error connect to: " + FString::FromInt(connect);
			GEngine->AddOnScreenDebugMessage(-1, 5.0f, FColor::Black, mess);
			closesocket(connect);

			for (size_t i = 0; i < Connections.size(); i++)
			{
				if (Connections[i] == connect && NameConnections[i].connection == connect)
				{
					GEngine->AddOnScreenDebugMessage(-1, 5.0f, FColor::Black, "Find!Ok!");
					auto iter = Connections.begin();
					Connections.erase(iter + i);
					auto iter2 = NameConnections.begin();
					NameConnections.erase(iter2 + i);
					break;
				}
			}*/

			break;
		}
		switch (FirstCode)
		{
		case '#'://сбросить клиента с учета
			DeleteConnections(connect);
			break;
		case 'a'://прием данных от станции и отправка этих данных другим участникам сети
			if (!Receiving_and_sending_data_for_stations(connect)) break;
			break;
		case 'b'://прием команды на исполнение на сервере(перевод стрелки или построение-отмена маршрута)

			Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&station, sizeof(Station));

			Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&comm, sizeof(Commd));

			RunCommandFromClient(station, comm);
			break;
		case 'd'://послать данные станции(недополученные) клиенту(который недополучил)

			Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&station, sizeof(Station));

			SendData(/*Connections[index].*/connect, station);
			break;
		case 'o': //установить оперативное количество вагонов для разделения
			Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&station, sizeof(Station));

			operCountVagForMinus = 0;
			Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&operCountVagForMinus, sizeof(int));

			operDataStations.SetOperCountVag(operCountVagForMinus, station);
			break;

		case'z'://выполнить запрос диспетчера на установку приоритета управленияя станцией
			Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&station, sizeof(Station));

			ZaprosSetDispetcherDirect(station);
			break;

		case'x': //выполнить запрос диспетчера на передачу управления станцией оператору
			Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&station, sizeof(Station));

			ZaprosSetOperatorDirect(station);
			break;

		case'v': //выполнить запрос на искусственную разделку
			Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&station, sizeof(Station));

			IskusstvennayaRazdelka(station);
			break;

		case 'f': //выполнить запрос с сортировки на освобождение номера поезда
			Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&NumberTrain, sizeof(int));

			Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&NumberWay, sizeof(int));

			ZaprosSetTrainFree(NumberTrain, NumberWay);
		case 's': //выполнить запрос с сортировки на поездную операцию

		/*	Sizebyte = MyRecv(connect, (char*)&check, sizeof(FOtcep));
			Sizebyte = MyRecv(connect, (char*)&check, sizeof(FOtcep));
			Sizebyte = MyRecv(connect, (char*)&check, sizeof(FOtcep));*/

			Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&zaprosSort, sizeof(ZaprosFromSortirovka));

			sizeOfData = sizeof(ZaprosFromSortirovka);

			if (Sizebyte == sizeOfData)
			{
				ZaprosSortirovka(zaprosSort);

				break;
			}

			//GEngine->AddOnScreenDebugMessage(-1, 10.0, FColor::Red, "Server:: Zapros sortirovka>>>    No sootvetstvie data");
			break;
		case '+'://выполнить запрос с сортировки на обновление TrainReestr

			Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&zaprosAddOtcep, sizeof(FDataZaprosAddOtcep));

			ZaprosUpdateTrainReestr(zaprosAddOtcep);
			break;
		case '-'://выполнить запрос с сортировки на обновление TrainReestr

			Sizebyte = MyRecv(/*Connections[index].*/connect, (char*)&zaprosAddOtcep, sizeof(FDataZaprosMinusOtcep));

			ZaprosUpdateTrainReestr(zaprosMinusOtcep);
			break;
		}
	}
}
