int ADisPawn::MySend(SOCKET connect, char* buf, int len)
{
	//mtxForSendToServer.lock();
	int total = 0;
	int n = 0;
	while (total < len)
	{
		n = send(connect, buf + total, len - total, NULL);
		if (n == SOCKET_ERROR)//проверяем возврат send на ошибку
		{
			DeleteConnections(connect);
		}
		if (n == -1) break;
		total += n;
	}
	return (n == -1 ? -1 : total);

	//mtxForSendToServer.unlock();
}

int ADisPawn::MyRecv(SOCKET connect, char* buf, int len)
{
	int total = 0;
	int n = 0;
	while (total < len)
	{
		n = recv(connect, buf + total, len - total, NULL);
		if (n == SOCKET_ERROR)//проверяем возврат send на ошибку
		{
			DeleteConnections(connect);
		}
		if (n == -1 || n == 0) break;
		total += n;
	}
	if (n == -1 || n == 0) return n;
	return total;
}

