void ADisPawn::SetTwoGreenKeys(FElm& GreenKey1, FElm& way1, FElm& way2, FElm& way3, FElm& way4, FElm& GreenKey2)
{
	if (way1.stat != EAStat::Busy
		&& way2.stat != EAStat::Busy
		&& way3.stat != EAStat::Busy
		&& way4.stat != EAStat::Busy)
	{
		GreenKey1.stat = EAStat::Green;
		GreenKey2.stat = EAStat::Green;
	}
	else
	{
		GreenKey1.stat = EAStat::Red;
		GreenKey2.stat = EAStat::Red;
	}
}

