void ADisPawn::SendCommToServer(Station station, Commd com)
{
	//если куратор то команда выполняется напрямую на сервере куратора
	if (Curator)
	{
		RunCommandFromClient(station, com);
	}
	else
	{
		char FirstCode = 'b';
		Commd command = com;
		MySend(Connection, (char*)&FirstCode, sizeof(char));
		MySend(Connection, (char*)&station, sizeof(Station));
		MySend(Connection, (char*)&command, sizeof(Commd));
	}
}
