std::wstring ADisPawn::GetDateNow()
{
	//Получить метку времени по Гринвичу
	time_t now = time(0);
	struct tm timeinfo;
	//Локализовать время
	localtime_s(&timeinfo, &now);
	//задать формат даты
	wchar_t format[] = L"%d.%m.%g";

	wchar_t buffer[80];
	//Вывести время по формату в буфер
	wcsftime(buffer, 80, format, &timeinfo);
	//Переписать буфер в объект string
	std::wstring str_buffer(buffer);
	return str_buffer;
}

std::wstring ADisPawn::GetTimeNow()
{
	//Получить метку времени по Гринвичу
	time_t now = time(0);
	struct tm timeinfo;
	//Локализовать время
	localtime_s(&timeinfo, &now);
	//задать формат даты
	wchar_t format[] = L" %H:%M";

	wchar_t buffer[80];
	//Вывести время по формату в буфер
	wcsftime(buffer, 80, format, &timeinfo);
	//Переписать буфер в объект string
	std::wstring str_buffer(buffer);
	return str_buffer;
}

