void ADisPawn::AutoUpdatePultInThread()
{
	while (cicleRun)
	{
		if (autoUpdateSet)
		{
			if (Karabas)
			{
				krb_pult_arrComm.clear();
				krb_SetDataForUpdate();
			}
			if (Karaganozek)
			{
				krz_pult_arrComm.clear();
				krz_SetDataForUpdate();
			}

		}
		Sleep(pauseAutoUpdate);
	}
}

FString ADisPawn::CreatePortHandler()
{
	FString str = "port COM 3";
	sPortName = L"COM3";
	hSerial = ::CreateFile(sPortName, GENERIC_READ | GENERIC_WRITE, 0, 0, OPEN_EXISTING, FILE_ATTRIBUTE_NORMAL, 0);
	if (hSerial == INVALID_HANDLE_VALUE)
	{

		return "Error port";
	}
	DCB dcbSerialParams = { 0 };
	dcbSerialParams.DCBlength = sizeof(dcbSerialParams);
	if (!GetCommState(hSerial, &dcbSerialParams))
	{
		//std::cout << "getting state error\n";

		return "Error port";
	}
	dcbSerialParams.BaudRate = CBR_9600;
	dcbSerialParams.ByteSize = 8;
	dcbSerialParams.StopBits = ONESTOPBIT;
	dcbSerialParams.Parity = NOPARITY;
	if (!SetCommState(hSerial, &dcbSerialParams))
	{
		std::cout << "error setting serial port state\n";
		return "Error port";
	}
	//namePort = sPortName;

	return str;
}
bool ADisPawn::IsPortWorkable()
{
	DCB dcbSerialParams = { 0 };
	dcbSerialParams.DCBlength = sizeof(dcbSerialParams);
	if (GetCommState(hSerial, &dcbSerialParams))
	{
		return true;
	}
	return false;
}
void ADisPawn::ClosePort()
{
	if (IsPortWorkable())
	{
		CloseHandle(hSerial);
	}
	/*BeepTest1();
	BeepTest1();*/
}

