bool ADisPawn::TrainReestrSave(FString name, TrainReestr reestrForSave)
{
	std::ofstream ofs;

	FString fName = name;
	std::string strPath(TCHAR_TO_UTF8(*fName));
	const char* charName = strPath.c_str();
	ofs.open(charName);

	if (!ofs.is_open())
	{
		//std::cout << "Ошибка открытия файла для записи\n";
		TrainReestrSave(name, reestrForSave);
		////BeeperBad(1);
		return false;
	}
	else
	{
		ofs.write((char*)&trainReestr, sizeof(TrainReestr));
		//BeeperGood3();
	}
	ofs.close();

	return true;
}
int ADisPawn::TrainReestrOpen(int name)
{
	int rez = -1;
	if (TrainReestrOpen(GetNameForSaving(name), &trainReestr)) return name;
	else return rez;
}
bool ADisPawn::TrainReestrOpen(FString name, TrainReestr* reestrForOpenFromSave)
{
	std::ifstream ifs;

	FString fName = name;
	std::string strPath(TCHAR_TO_UTF8(*fName));
	const char* charName = strPath.c_str();
	ifs.open(charName);

	if (!ifs.is_open())
	{
		TrainReestrSave(name, *reestrForOpenFromSave);
		BeeperBad(2);
		ifs.close();
		return false;
	}
	else
	{
		ifs.read((char*)reestrForOpenFromSave, sizeof(TrainReestr));
		//BeeperGood3();
	}
	return true;

}

