bool ADisPawn::SetOtcepForSelectedTrain(int indexSelectedTypeVagon, int indexSelectedcount, int indexSelectedStation, int indexSelectedEndWay)
{
	if (selectedTrain == nullptr) return false;
	ETypeVagon type = GetVagonTypeFromSelectedIndex(indexSelectedTypeVagon);
	int count = indexSelectedcount + 1;
	Station station = GetStationFromSelectedIndex(indexSelectedStation);
	TargetEndWay way = GetTargetEndWayFromSelectedIndex(indexSelectedEndWay);
	if (type == ETypeVagon::notDefine) return false;
	if (count <= 0) return false;
	if (station == Station::Unknown) return false;
	if (way == TargetEndWay::no) return false;
	selectedTrain->SetTrainReady();
	return selectedTrain->SetOtcep(type, count, station, way);
}

