bool ADisPawn::LocoOnWay(FElm* way)
{
	int numberLoco = rand() % 89 + 10;
	if (way->stat == EAStat::Free)
	{
		way->stat = EAStat::Busy;
		way->dopn = numberLoco;
	}
	else
	{
		way->stat = EAStat::Free;
		way->dopn = 0;
	}
	return true;
}
bool ADisPawn::SostavOnWay(FElm* way)
{
	if (selectedTrain == nullptr) return false;
	int numberTrain = GetSelectedTrain()->GetNumber();
	selectedTrain->SetOnRels();
	selectedTrain = nullptr;

	if (way->stat == EAStat::Free)
	{
		way->stat = EAStat::Busy;
		way->num = numberTrain;
		//way->dopn = numberLoco;
	}
	else
	{
		way->stat = EAStat::Free;
		way->num = 0;
		way->dopn = 0;
	}
	return true;
}
