bool ADisPawn::IsWayFree(FElm& way)
{
	if (&way == nullptr) return false;
	if (way.num == 0 && way.dopn == 0) return true;
	return false;
}

TArray<FString> ADisPawn::RemoveBuzy(FString station, TArray<FString> arrWay)
{
	TArray<FString> arrFreeWay;
	for (size_t i = 0; i < arrWay.Num(); i++)
	{
		if (IsWayFree(GetWay(station, arrWay[i])) == true)
		{
			arrFreeWay.Add(arrWay[i]);
		}
	}
	return arrFreeWay;
}

TArray<FString> ADisPawn::RemoveFree(FString station, TArray<FString> arrWay)
{
	TArray<FString> arrBuzyWay;
	for (size_t i = 0; i < arrWay.Num(); i++)
	{
		if (IsWayFree(GetWay(station, arrWay[i])) == false)
		{
			arrBuzyWay.Add(arrWay[i]);
		}
	}
	return arrBuzyWay;
}

