void ADisPawn::ZaprosSortirovka(ZaprosFromSortirovka zapros)
{

	FElm* way = GetParkWay(zapros.numEndWay);
	Train* startTrain = trainReestr.GetTrain(zapros.numOldTrain);
	Train* endTrain = nullptr;

	if (way == nullptr || startTrain == nullptr) return;

	if (way->num == 0)
	{
		endTrain = trainReestr.GetFreeTrainForZaprosSort();
		if (endTrain == nullptr) return;
		int newNumTrain = 8800;
		for (size_t i = 0; i < 200; i += 2)
		{
			newNumTrain += i;
			if (trainReestr.IsNumberFree(newNumTrain)) break;
		}
		endTrain->SetNumber(newNumTrain);
		endTrain->SetOnRels();
		way->num = newNumTrain;
		way->stat = EAStat::Busy;
	}
	else if (way->num != 0)
	{
		endTrain = trainReestr.GetTrain(way->num);
		if (endTrain == nullptr) return;
	}

	endTrain->AddOtcepToTail(zapros.otcep);
	startTrain->RemoveOtcep(zapros.otcep);
	if (startTrain->GetOtcepCount() == 0)
	{
		if (startTrain->GetNumber() == kg1._p1g1p.num)
		{
			kg1._p1g1p.num = 0;
			kg1._p1g1p.stat = EAStat::Free;
		}
		if (startTrain->GetNumber() == kg1._p1g2p.num)
		{
			kg1._p1g2p.num = 0;
			kg1._p1g2p.stat = EAStat::Free;
		}
		startTrain->SetTrainFree();

		SendDataStationToAll(Station::Karaganda_1);
	}

	SendDataStationToAll(Station::Karaganda_2);
	SendDataStationToAll(Station::TrainReestr);
}
