void ADisPawn::ZaprosUpdateTrainReestr(FDataZaprosAddOtcep zaprosAddOtcep)
{
	FElm* parkWay = GetParkWayFromZaprosSort(zaprosAddOtcep.endWay);
	Train* groupOtcep = nullptr;

	FOtcep* operOtcep = nullptr;
	if (parkWay == nullptr) return;
	if (parkWay->num == 0)
	{
		groupOtcep = trainReestr.GetFreeOddGroupOtcepFromReestr();
		if (groupOtcep == nullptr) return;

		parkWay->num = groupOtcep->GetNumber();
	}
	else
	{
		groupOtcep = trainReestr.GetTrain(parkWay->num);
		if (groupOtcep == nullptr) return;
	}
	if (groupOtcep->GetNumber() % 2 != 0)
	{
		groupOtcep->AddOtcepToTail(zaprosAddOtcep.otcep);
	}
	else
	{
		groupOtcep->AddOtcepToHead(zaprosAddOtcep.otcep);
	}

	SendDataStationToAll(Station::Karaganda_2);
	SendDataStationToAll(Station::TrainReestr);
}
