bool ADisPawn::IsStartWayNotCHAPorCHPorTD(Router* router)
{
	FElm* startWay = router->arrRouter[0];
	if (startWay == &krb._CHP || startWay == &krb._CHAP || startWay == &krb._NP) return false;
	if (startWay == &krz._CHP || startWay == &krz._CHAP || startWay == &krz._NDP || startWay == &krz._NAP || startWay == &krz._CHRP) return false;
	if (startWay == &kg._CHP || startWay == &kg._CHAP || startWay == &kg._NP || startWay == &kg._NAP) return false;
	if (startWay == &mkd._CHP || startWay == &mkd._CHAP || startWay == &mkd._NP || startWay == &mkd._NAP) return false;
	if (startWay == &gk._CHP || startWay == &gk._CHAP || startWay == &gk._NP || startWay == &gk._NAP) return false;
	if (startWay == &kg2._CHP || startWay == &kg2._CHAP || startWay == &kg1._NP || startWay == &kg1._NAP) return false;
	if (startWay == &rs._CHP  || startWay == &rs._NP || startWay == &rs._NAP) return false;
	if (startWay == &dbs._CHP ||  startWay == &dbs._NP ) return false;
	if (startWay == &gu._CHP || startWay == &gu._CHAP || startWay == &gu._NP ) return false;
	if (startWay == &ru._CHP || startWay == &ru._NP ) return false;
	


	return true;
}
void ADisPawn::RunTrainToTrain(Router* router, Station station)
{
	//все проверки на возможность стыковки уже были проведены
	router->arrRouter[0]->oper = EOper::WaitingStart;
	int numMainTrain = GetEndRouter(router)->num;
	int numAddGroup = GetWayStart(router)->num;
	Train* mainTrain = nullptr;
	Train* addGroup = nullptr;
	mainTrain = trainReestr.GetTrain(numMainTrain);
	addGroup = trainReestr.GetTrain(numAddGroup);
	if (mainTrain == nullptr || addGroup == nullptr) return;
	EdopStat dopstat = router->GetRouterDopstat();
	bool isRouterOdd = IsRouter_Odd(dopstat);
	int numLoco = 0;

	//светофор по умолчанию
	router->SetSvetoforDefault();
	//начало маршрута -  по умолчанию
	router->arrRouter[0]->dopstat = EdopStat::Norm;
	router->arrRouter[0]->oper = EOper::Norm;
	router->arrRouter[0]->num = 0;
	router->arrRouter[0]->dopn = 0;
	//занятость путей
	for (size_t i = 2; i < router->sizeRouter; i++)
	{

		router->arrRouter[i]->stat = EAStat::Busy;
		if (station == Station::Karaganda_1 || station == Station::Karaganda_2) SendDataTwoStationToAll(Station::Karaganda_1, Station::Karaganda_2);
		else SendDataStationToAll(station);
		Sleep(cur.timeforRouterRun * 100);
	}
	//теперь - освобождение путей	
	router->arrRouter[0]->stat = EAStat::Free;
	for (size_t i = 2; i < router->sizeRouter; i++)
	{
		if (i != router->sizeRouter - 1)
		{
			router->arrRouter[i]->stat = EAStat::Free;
		}
		router->arrRouter[i]->dopstat = EdopStat::Norm;
		if (station == Station::Karaganda_1 || station == Station::Karaganda_2) SendDataTwoStationToAll(Station::Karaganda_1, Station::Karaganda_2);
		else SendDataStationToAll(station);
		Sleep(cur.timeforRouterRun * 100);
	}
	//операция стыковки по отцепам
	trainReestr.AddGroupOtcepToTrain(mainTrain, addGroup, isRouterOdd);

	//разбираемся с локомотивами
	//при нечетном маршруте отнимаем у группы четный локомотив и пристыковываем к четной стороне поезда и наоборот
	if (isRouterOdd)
	{
		numLoco = addGroup->GetNumberLocoEven();
		addGroup->SetNumberLocoEven(0);
		mainTrain->SetNumberLocoEven(numLoco);
	}
	else
	{
		numLoco = addGroup->GetNumberLocoOdd();
		addGroup->SetNumberLocoOdd(0);
		mainTrain->SetNumberLocoOdd(numLoco);
	}

	//разбираемся с номерами (!!! возможна пристыковка поезда к стоящей группе) 
	//классический случай:   пристыковываем группу к поезду и освобождаем группу
	if (numAddGroup < 1000 || (numMainTrain > 1000 && numAddGroup > 1000)) //группа к чему - либо или поезд к поезду
	{
		addGroup->SetTrainFree();
	}
	else if (numMainTrain < 1000 && numAddGroup > 1000)//поезд к группе
	{
		addGroup->SetTrainFree();
		addGroup->SetTrain(*mainTrain);
		mainTrain->SetTrainFree();
		router->arrRouter[router->sizeRouter - 1]->num = addGroup->GetNumber();
		SendDataStationToAll(station);
		Sleep(30);
	}


	//обновляем реестр для всех
	SendDataStationToAll(Station::TrainReestr);
}
