bool ADisPawn::One_Step_Smart_Router(Station from, Router* router)
{
	if (router->arrRouter[0]->dopstat != EdopStat::Norm)
	{
		DopConturKey_Unset(from, router->arrRouter[0]->dopstat);
		router->arrRouter[0]->dopstat = EdopStat::Norm;
	}
	for (size_t i = 0; i < router->sizeRouter - 1; i++)
	{

		if (i == router->sizeRouter - 2) router->arrRouter[i]->oper = EOper::Norm;

		if (router->arrRouter[i]->num != 0)
		{
			if (router->arrRouter[i + 1]->stat == EAStat::Free)
			{
				router->arrRouter[i + 1]->stat = EAStat::Busy;
				router->arrRouter[i + 1]->oper = EOper::WaitingStart;
				router->arrRouter[i + 1]->num = router->arrRouter[i]->num;
				//BeeperGood1();
				return true;
			}
			if (router->arrRouter[i]->num == router->arrRouter[i + 1]->num)
			{
				router->arrRouter[i]->stat = EAStat::Free;
				router->arrRouter[i]->num = 0;
				router->arrRouter[i + 1]->oper = EOper::Norm;
				//BeeperGood2();
				return true;
			}
		}
		if (router->arrRouter[i]->dopn != 0)
		{
			if (router->arrRouter[i + 1]->stat == EAStat::Free)
			{
				router->arrRouter[i + 1]->stat = EAStat::Busy;
				router->arrRouter[i + 1]->oper = EOper::WaitingStart;
				router->arrRouter[i + 1]->dopn = router->arrRouter[i]->dopn;
				//BeeperGood1();
				return true;
			}
			if (router->arrRouter[i]->dopn == router->arrRouter[i + 1]->dopn)
			{
				router->arrRouter[i]->stat = EAStat::Free;
				router->arrRouter[i]->dopn = 0;
				router->arrRouter[i + 1]->oper = EOper::Norm;
				//BeeperGood2();
				return true;
			}
		}
	}
	return false;
}


