bool ADisPawn::IsThereLocoForMoving(FElm* startWay)
{
	if (startWay->dopn != 0) return true;
	else if (startWay->num != 0)
	{
		int numCheckTrain = startWay->num;
		Train* checkTrain = nullptr;
		checkTrain = trainReestr.GetTrain(numCheckTrain);
		if (checkTrain == nullptr) return false;
		else
		{
			if (checkTrain->IsThereLocoEven()) return true;
			if (checkTrain->IsThereLocoOdd()) return true;
		}
	}
	return false;

}
FString ADisPawn::GetNumbersLocoInSostav(int dopn)
{
	dopn = abs(dopn);
	if (dopn < 100) return FString::FromInt(dopn);
	else if (dopn < 10000) return FString::FromInt(dopn / 100) + " " + FString::FromInt(dopn % 100);
	else return FString::FromInt(dopn / 10000) + " " + FString::FromInt((dopn % 10000) / 100) + " " + FString::FromInt(dopn % 100);
}
