void ADisPawn::ru_ToMinusInThread(FElm* str)
{
	//проверка на необходимость перевода
	if (str->stat == EAStat::Minus) return;

	//проверка на возможность перевода
	if (!ru_IsStrelkaFree(str)) return;

	//при необходимости получаем вторую стрелку с уже определеннным порядком перевода
	FElm* str1 = str;
	FElm* str2 = nullptr;
	ru_GetTwiceStrelka(str1, str2);

	//начинаем перевод
	str1->stat = EAStat::Status_changes;
	Sleep(10);
	SendDataStationToAll(Station::Radiouzel);
	Sleep(cur.SwitchingTime * 1000);

	str1->stat = EAStat::Minus;
	SendDataStationToAll(Station::Radiouzel);
	Sleep(cur.SwitchingTime * 100);

	if (str2 == nullptr) return;

	//начинаем перевод второй
	str2->stat = EAStat::Status_changes;
	SendDataStationToAll(Station::Radiouzel);
	Sleep(cur.SwitchingTime * 1000);

	str2->stat = EAStat::Minus;
	SendDataStationToAll(Station::Radiouzel);
}

