bool ADisPawn::rs_IsStrelkaFree(FElm* str)
{
	if (str->num == 1)
	{
		if (WayFree(rs._str1, rs._s1, rs._M1_s1)) return true;
		else return false;
	}

	if (str->num == 2)
	{
		if (WayFree(rs._str2, rs._s2, rs._M2_s2)) return true;
		else return false;
	}

	if (str->num == 3 || str->num == 7)
	{
		if (WayFree(rs._str3, rs._s3, rs._s1_s3, rs._str7, rs._s7, rs._CH3_s3)) return true;
		else return false;
	}

	if (str->num == 4 || str->num == 10)
	{
		if (WayFree(rs._str4, rs._s4, rs._s2_s4, rs._str10, rs._s10, rs._N3_s4)) return true;
		else return false;
	}

	if (str->num == 5)
	{
		if (WayFree(rs._str5, rs._s5, rs._s3_s5)) return true;
		else return false;
	}

	if (str->num == 6)
	{
		if (WayFree(rs._str6, rs._s6, rs._N1_s6)) return true;
		else return false;
	}

	if (str->num == 8)
	{
		if (WayFree(rs._str8, rs._s8, rs._M3_s8)) return true;
		else return false;
	}


	return false;
}

