bool ADisPawn::gu_IsStrelkaFree(FElm* str)
{
	if (str->num == 1)
	{
		if (WayFree(gu._str1, gu._s1, gu._M1_s1)) return true;
		else return false;
	}

	if (str->num == 2 || str->num == 4)
	{
		if (WayFree(gu._str2, gu._s2, gu._M2_s2, gu._str4, gu._s4, gu._s4_s6)) return true;
		else return false;
	}


	if (str->num == 3)
	{
		if (WayFree(gu._str3, gu._s3, gu._s1_s3)) return true;
		else return false;
	}

	if (str->num == 5)
	{
		if (WayFree(gu._str5, gu._s5, gu._s5_s7)) return true;
		else return false;
	}

	if (str->num == 6)
	{
		if (WayFree(gu._str6, gu._s6, gu._s6_s8)) return true;
		else return false;
	}

	if (str->num == 7)
	{
		if (WayFree(gu._str7, gu._s7, gu._s5_s7)) return true;
		else return false;
	}

	if (str->num == 8 || str->num == 10)
	{
		if (WayFree(gu._str8, gu._s8, gu._s6_s8, gu._str10, gu._s10, gu._s10_s16)) return true;
		else return false;
	}

	if (str->num == 9)
	{
		if (WayFree(gu._str9, gu._s9, gu._CH2_s9)) return true;
		else return false;
	}

	if (str->num == 10)
	{
		if (WayFree(gu._str10, gu._s10, gu._s10_s16)) return true;
		else return false;
	}


	if (str->num == 11)
	{
		if (WayFree(gu._str11, gu._s11, gu._CH1_s11)) return true;
		else return false;
	}

	if (str->num == 12)
	{
		if (WayFree(gu._str12, gu._s12, gu._s12_s14)) return true;
		else return false;
	}

	if (str->num == 14)
	{
		if (WayFree(gu._str14, gu._s14, gu._N2_s14)) return true;
		else return false;
	}

	if (str->num == 16)
	{
		if (WayFree(gu._str16, gu._s16, gu._N4_s16)) return true;
		else return false;
	}
	return false;
}

