void ADisPawn::krb_Pult_LoaderTo()
{
	std::ofstream ofs;

	if (krb_pult_arrComm.size() != 0)
	{
		ofs.open(wpathToDocuments + L"\\LogCommandToDiod.txt", std::ios::app);
		if (!ofs.is_open())
		{
			BeeperBad(1);
		}
		else
		{
			ofs << krb_pult_arrComm[0].c_str();
		}
		iRet = WriteFile(hSerial, krb_pult_arrComm[0].c_str(), krb_pult_arrComm[0].size(), &dwBytesWritten, NULL);
		krb_pult_arrComm.erase(krb_pult_arrComm.begin());
		if (cur.IsKarabasVoiceToPultCommandSet)
		{
			MyBeep(400, 100);
		}
	}
	ofs.close();
}
