void ADisPawn::krz_Pult_CheckerChanger()
{
	for (size_t i = 0; i < krz_pult_arrPelmStr.size(); i++)
	{
		if (krz_pult_arrPelmStr[i].elm->stat != krz_pult_arrStrDub[i].stat || krz_pult_arrPelmStr[i].elm->oper != krz_pult_arrStrDub[i].oper)
		{
			krz_pult_arrStrDub[i].stat = krz_pult_arrPelmStr[i].elm->stat;
			krz_pult_arrStrDub[i].oper = krz_pult_arrPelmStr[i].elm->oper;
			krz_Pult_SenderTo_LoaderTo(krz_pult_arrPelmStr[i]);
		}
	}
	for (size_t i = 0; i < krz_pult_arrPelmSvetofor.size(); i++)
	{
		if (krz_pult_arrPelmSvetofor[i].elm->stat != krz_pult_arrSvetoforDub[i].stat || krz_pult_arrPelmSvetofor[i].elm->oper != krz_pult_arrSvetoforDub[i].oper)
		{
			krz_pult_arrSvetoforDub[i].stat = krz_pult_arrPelmSvetofor[i].elm->stat;
			krz_pult_arrSvetoforDub[i].oper = krz_pult_arrPelmSvetofor[i].elm->oper;
			krz_Pult_SenderTo_LoaderTo(krz_pult_arrPelmSvetofor[i]);
		}
	}
	for (size_t i = 0; i < krz_pult_arrPelmWay.size(); i++)
	{
		if (krz_pult_arrPelmWay[i].elm->stat != krz_pult_arrWayDub[i].stat)
		{
			krz_pult_arrWayDub[i].stat = krz_pult_arrPelmWay[i].elm->stat;
			krz_Pult_SenderTo_LoaderTo(krz_pult_arrPelmWay[i]);
		}
	}
	for (size_t i = 0; i < krz_pult_arrPelmSignals.size(); i++)
	{
		if (krz_pult_arrPelmSignals[i].elm->stat != krz_pult_arrEFlmSignalsDub[i].stat
			|| krz_pult_arrPelmSignals[i].elm->dopstat != krz_pult_arrEFlmSignalsDub[i].dopstat
			|| krz_pult_arrPelmSignals[i].elm->oper != krz_pult_arrEFlmSignalsDub[i].oper
			|| krz_pult_arrPelmSignals[i].elm->cur != krz_pult_arrEFlmSignalsDub[i].cur)
		{
			krz_pult_arrEFlmSignalsDub[i].stat = krz_pult_arrPelmSignals[i].elm->stat;
			krz_pult_arrEFlmSignalsDub[i].dopstat = krz_pult_arrPelmSignals[i].elm->dopstat;
			krz_pult_arrEFlmSignalsDub[i].oper = krz_pult_arrPelmSignals[i].elm->oper;
			krz_pult_arrEFlmSignalsDub[i].cur = krz_pult_arrPelmSignals[i].elm->cur;

			krz_Pult_SenderTo_LoaderTo(krz_pult_arrPelmSignals[i]);
		}
	}
	for (size_t i = 0; i < krz_pult_arrBoolSignals.size(); i++)
	{
		if (*(krz_pult_arrBoolSignals[i].boo) != krz_pult_arrBoolSignalsDub[i])
		{
			krz_pult_arrBoolSignalsDub[i] = *(krz_pult_arrBoolSignals[i].boo);

			krz_Pult_SenderTo_LoaderTo(krz_pult_arrBoolSignals[i]);
		}
	}
}
