void ADisPawn::krz_Pult_LoaderTo()
{

	std::ofstream ofs;
	if (krz_pult_arrComm.size() != 0)
	{
		ofs.open(wpathToDocuments + L"\\LogCommandToDiod.txt", std::ios::app);
		if (!ofs.is_open())
		{
			BeeperBad(1);
		}
		else
		{
			time_t     now = time(0);
			struct tm  tstruct;
			char       buf[80];
			localtime_s(&tstruct, &now);
			// Visit http://en.cppreference.com/w/cpp/chrono/c/strftime
			// for more information about date/time format
			strftime(buf, sizeof(buf), "%Y-%m-%d %X", &tstruct);



			ofs << buf;

			ofs << "\t";
			ofs << krz_pult_arrComm[0].c_str();
		}

		iRet = WriteFile(hSerial, krz_pult_arrComm[0].c_str(), krz_pult_arrComm[0].size(), &dwBytesWritten, NULL);
		krz_pult_arrComm.erase(krz_pult_arrComm.begin());

	}
	else if (krz_pult_arrComm.size() == 0 && krz_pult_arrCommDub.size() != 0)
	{
		ofs.open(wpathToDocuments + L"\\LogCommandToDiod.txt", std::ios::app);
		if (!ofs.is_open())
		{
			BeeperBad(1);
		}
		else
		{
			ofs << krz_pult_arrComm[0].c_str();
		}
		iRet = WriteFile(hSerial, krz_pult_arrCommDub[0].c_str(), krz_pult_arrCommDub[0].size(), &dwBytesWritten, NULL);

		krz_pult_arrCommDub.erase(krz_pult_arrCommDub.begin());
	}
	ofs.close();

}
