void ADisPawn::krz_Pult_CheckerInThread()
{
	DCB dcbSerialParams = { 0 };
	dcbSerialParams.DCBlength = sizeof(dcbSerialParams);
	cicleRun = true;
	int counter = 0;
	while (cicleRun)
	{
		if (GetCommState(hSerial, &dcbSerialParams))
		{
			krz_Pult_LoaderTo();
			counter++;
			if (counter == 50)
			{
				ClosePort();
				CreatePortHandler();
				counter = 0;
			}
		}
		Sleep(100);
	}
}

