bool ADisPawn::kg_IsStrelkaFree(FElm* str)
{
	if (str->num == 1 || str->num == 3)
	{
		if (WayFree(kg._str1, kg._s1, kg._M3_s1, kg._str3, kg._s3, kg._M1_s3)) return true;
		else return false;
	}

	if (str->num == 2 || str->num == 4)
	{
		if (WayFree(kg._str2, kg._s2, kg._M2_s2, kg._str4, kg._s4, kg._s4_s6)) return true;
		else return false;
	}


	if (str->num == 5 || str->num == 7)
	{
		if (WayFree(kg._str5, kg._s5, kg._s3_s5, kg._str7, kg._s7, kg._CH4_s7)) return true;
		else return false;
	}

	if (str->num == 6 || str->num == 8)
	{
		if (WayFree(kg._str6, kg._s6, kg._s6_s14, kg._str8, kg._s8, kg._N4_s8)) return true;
		else return false;
	}


	if (str->num == 11)
	{
		if (WayFree(kg._str11, kg._s11, kg._CH3_s11)) return true;
		else return false;
	}



	if (str->num == 13 || str->num == 15)
	{
		if (WayFree(kg._str13, kg._s13, kg._CH2_s13, kg._str15, kg._s15, kg._CH1_s13)) return true;
		else return false;
	}

	if (str->num == 14)
	{
		if (WayFree(kg._str14, kg._s14, kg._s6_s14)) return true;
		else return false;
	}

	if (str->num == 17)
	{
		if (WayFree(kg._s17, kg._s15_s17)) return true;
		else return false;
	}

	if (str->num == 18 || str->num == 16)
	{
		if (WayFree(kg._str18, kg._s18, kg._N2_s18, kg._str16, kg._s16, kg._N1_s18)) return true;
		else return false;
	}



	if (str->num == 18)
	{
		if (WayFree(kg._str18, kg._s18, kg._s16_s18)) return true;
		else return false;
	}

	if (str->num == 20)
	{
		if (WayFree(kg._str20, kg._s20, kg._s16_s20)) return true;
		else return false;
	}

	return false;
}

