void ADisPawn::mkd_GetTwiceStrelka(FElm*& str1, FElm*& str2)
{
	if (str1->num == 1 || str1->num == 3)
	{
		str1 = &mkd._str3;
		str2 = &mkd._str1;
	}
	else if (str1->num == 2 || str1->num == 4)
	{
		str1 = &mkd._str4;
		str2 = &mkd._str2;
	}
	else if (str1->num == 5 || str1->num == 7)
	{
		str1 = &mkd._str7;
		str2 = &mkd._str5;
	}
	else if (str1->num == 6 || str1->num == 8)
	{
		str1 = &mkd._str8;
		str2 = &mkd._str6;
	}
	else if (str1->num == 9 || str1->num == 15)
	{
		str1 = &mkd._str15;
		str2 = &mkd._str9;
	}
	else if (str1->num == 14 || str1->num == 16)
	{
		str1 = &mkd._str16;
		str2 = &mkd._str14;
	}
	else if (str1->num == 13 || str1->num == 19)
	{
		str1 = &mkd._str19;
		str2 = &mkd._str13;
	}
	else if (str1->num == 12 || str1->num == 20)
	{
		str1 = &mkd._str20;
		str2 = &mkd._str12;
	}
}

void ADisPawn::mkd_mkd_ToMinusInThread(FElm* str)
{
	//проверка на необходимость перевода
	if (str->stat == EAStat::Minus) return;

	//проверка на возможность перевода
	if (!mkd_IsStrelkaFree(str)) return;

	//при необходимости получаем вторую стрелку с уже определеннным порядком перевода
	FElm* str1 = str;
	FElm* str2 = nullptr;
	mkd_GetTwiceStrelka(str1, str2);

	//начинаем перевод
	str1->stat = EAStat::Status_changes;
	Sleep(10);
	SendDataStationToAll(Station::Maykuduk);
	Sleep(cur.SwitchingTime * 1000);

	str1->stat = EAStat::Minus;
	SendDataStationToAll(Station::Maykuduk);
	Sleep(cur.SwitchingTime * 100);

	if (str2 == nullptr) return;

	//начинаем перевод второй
	str2->stat = EAStat::Status_changes;
	SendDataStationToAll(Station::Maykuduk);
	Sleep(cur.SwitchingTime * 1000);

	str2->stat = EAStat::Minus;
	SendDataStationToAll(Station::Maykuduk);
}

