bool ADisPawn::mkd_IsStrelkaFree(FElm* str)
{
	if (str->num == 1 || str->num == 3)
	{
		if (WayFree(mkd._str1, mkd._s1, mkd._M3_s1, mkd._str3, mkd._s3, mkd._M1_s3)) return true;
		else return false;
	}

	if (str->num == 2 || str->num == 4)
	{
		if (WayFree(mkd._str2, mkd._s2, mkd._M2_s2, mkd._str4, mkd._s4, mkd._s4_s6)) return true;
		else return false;
	}


	if (str->num == 5 || str->num == 7)
	{
		if (WayFree(mkd._str5, mkd._s5, mkd._s3_s5, mkd._str7, mkd._s7, mkd._s7_s9)) return true;
		else return false;
	}

	if (str->num == 6 || str->num == 8)
	{
		if (WayFree(mkd._str6, mkd._s6, mkd._s6_s14, mkd._str8, mkd._s8, mkd._s8_s10)) return true;
		else return false;
	}



	if (str->num == 9 || str->num == 15)
	{
		if (WayFree(mkd._str9, mkd._s9, mkd._s7_s9, mkd._str15, mkd._s15, mkd._s15_s17)) return true;
		else return false;
	}

	if (str->num == 10)
	{
		if (WayFree(mkd._str10, mkd._s10, mkd._s8_s10)) return true;
		else return false;
	}


	if (str->num == 11)
	{
		if (WayFree(mkd._str11, mkd._s11, mkd._CH3_s11)) return true;
		else return false;
	}

	if (str->num == 12 || str->num == 20)
	{
		if (WayFree(mkd._str12, mkd._s12, mkd._N5_s12, mkd._str20, mkd._s20, mkd._N6_s12)) return true;
		else return false;
	}

	if (str->num == 13 || str->num == 19)
	{
		if (WayFree(mkd._str13, mkd._s13, mkd._CH2_s13, mkd._str19, mkd._s19, mkd._CH1_s13)) return true;
		else return false;
	}


	if (str->num == 14 || str->num == 16)
	{
		if (WayFree(mkd._str14, mkd._s14, mkd._s6_s14, mkd._str16, mkd._s16, mkd._s16_s18)) return true;
		else return false;
	}

	if (str->num == 17)
	{
		if (WayFree(mkd._str17, mkd._s17, mkd._s15_s17)) return true;
		else return false;
	}

	if (str->num == 18)
	{
		if (WayFree(mkd._str18, mkd._s18, mkd._s16_s18)) return true;
		else return false;
	}


	return false;
}
