bool ADisPawn::gk_IsStrelkaFree(FElm* str)
{
	if (str->num == 1 || str->num == 3)
	{
		if (WayFree(gk._str1, gk._s1, gk._M3_s1, gk._str3, gk._s3, gk._M1_s3)) return true;
		else return false;
	}

	if (str->num == 2 || str->num == 4)
	{
		if (WayFree(gk._str2, gk._s2, gk._M2_s2, gk._str4, gk._s4, gk._s4_s6)) return true;
		else return false;
	}


	if (str->num == 5 || str->num == 7)
	{
		if (WayFree(gk._str5, gk._s5, gk._s3_s5, gk._str7, gk._s7, gk._s7_s9)) return true;
		else return false;
	}

	if (str->num == 6 || str->num == 8)
	{
		if (WayFree(gk._str6, gk._s6, gk._s6_s14, gk._str8, gk._s8, gk._s8_s10)) return true;
		else return false;
	}



	if (str->num == 9 || str->num == 15)
	{
		if (WayFree(gk._str9, gk._s9, gk._s7_s9, gk._str15, gk._s15, gk._CH5_s15)) return true;
		else return false;
	}

	if (str->num == 10)
	{
		if (WayFree(gk._str10, gk._s10, gk._s8_s10)) return true;
		else return false;
	}


	if (str->num == 11)
	{
		if (WayFree(gk._str11, gk._s11, gk._CH3_s11)) return true;
		else return false;
	}



	if (str->num == 13 || str->num == 19)
	{
		if (WayFree(gk._str13, gk._s13, gk._CH2_s13)) return true;
		else return false;
	}


	if (str->num == 14 || str->num == 16)
	{
		if (WayFree(gk._str14, gk._s14, gk._s6_s14, gk._str16, gk._s16, gk._s16_s18)) return true;
		else return false;
	}

	if (str->num == 20)
	{
		if (WayFree(gk._s20, gk._N5_s10)) return true;
		else return false;
	}

	if (str->num == 18)
	{
		if (WayFree(gk._str18, gk._s18, gk._s16_s18)) return true;
		else return false;
	}

	if (str->num == 22)
	{
		if (WayFree(gk._str22, gk._s22, gk._N1_s18)) return true;
		else return false;
	}


	return false;
}

